var.rule:
var dr = ${SlimRoots.DeadRootRatePerLayer};
var n = ${NPKDemandSlimNP.rRNLDRT};		
if((${Phenology.sDVS} ge 2) and not ${DefaultManagement.DayAfterHarvest}){n = n+ ${NPKDemandSlimNP.sANRT};}		
var drsum = array:sum(dr);
var f = 0.0;
if(drsum > 0){f = n/drsum};
return array:calc(dr,'*',f);

var dr = ${SlimRoots.DeadRootRatePerLayer};
var p = ${NPKDemandSlimNP.rRPLDRT};				
if((${Phenology.sDVS} ge 2) and not ${DefaultManagement.DayAfterHarvest}){p = p+ ${NPKDemandSlimNP.sAPRT};}		
var drsum = array:sum(dr);
var f = 0.0;
if(drsum > 0){f = p/drsum};
return array:calc(dr,'*',f);
